<?phpclass adminservices extends controller{    public function __construct()    {        if (!model::checkadmin()) {            header("location: " . URL . "/login");        }    }    public function index()    {        $services = $this->model->get_services();        $data = ['services' => $services];        $this->viewUrl('admin/services/index', $data);    }    public function create()    {        $this->viewUrl('admin/services/create');    }    public function add()    {        if (isset($_POST['title']) && $_POST['description'] && $_FILES['image']) {            $this->model->add_services($_POST['title'], $_POST['description'], $_FILES['image']);        }        header('location: ' . URL . '/adminservices/create');    }    public function edit($id)    {        $service = $this->model->get_service($id);        $data = ['service' => $service];        $this->viewUrl('admin/services/edit', $data);    }    public function update($id)    {        if (isset($_POST['title']) && $_POST['description']) {            $this->model->update_services($id, $_POST['title'], $_POST['description'], $_FILES['image']);        }        header('location: ' . URL . '/adminservices/edit/' . $id);    }    public function delete($id)    {        $service = $this->model->get_service($id);        $this->model->delete_service($id , $service['image']);        header('location: ' . URL . '/adminservices');    }}