<?phpclass adminsettings extends controller{    public function __construct()    {        if (!model::checkadmin()) {            header("location: " . URL . "/login");        }    }    public function index()    {        $settings = $this->model->get_settings();        $data = ['settings' => $settings];        $this->viewUrl('admin/settings/index', $data);    }    public function create()    {        $this->viewUrl('admin/settings/create');    }    public function add()    {        if (isset($_POST['setting']) && $_POST['value']) {            $this->model->add_settings($_POST['setting'], $_POST['value']);        }        header('location: ' . URL . '/adminsettings/create');    }    public function edit($id)    {        $setting = $this->model->get_setting($id);        $data = ['setting' => $setting];        $this->viewUrl('admin/settings/edit', $data);    }    public function update($id)    {        if (isset($_POST['value']) && $_POST['setting']) {            $this->model->update_settings($id, $_POST['setting'], $_POST['value']);        }        header('location: ' . URL . '/adminsettings/edit/' . $id);    }    public function delete($id)    {        $this->model->delete_settings($id);        header('location: ' . URL . '/adminsettings');    }}