<?phpclass login extends controller{    public function index()    {        $this->viewUrl('login/index');    }    public function verify()    {        if (isset($_POST['email']) && isset($_POST['password'])) {            if ($this->model->checkPassword($_POST['email'], $_POST['password'])) {                model::session_init();                model::set_session('admin', $_POST['email']);                header("location: " . URL . "/adminsampleworks");            } else {                header("location: " . URL . "/index");            }        } else {            header("location: " . URL . "/login");        }    }}