<?phpclass samplework extends controller{    public function __construct()    {    }    public function index($service_id)    {        $sampleworks = $this->model->paginate($service_id);        $paginateRows = $this->model->paginateRows($service_id);        $service = $this->model->get_service($service_id);        $data = [            'sampleworks' => $sampleworks,            'paginateRows' => $paginateRows,            'service' =>$service        ];        $this->viewUrl('sampleworks/index' , $data);    }}