<?phpclass model_adminsampleworks extends model{    public function __construct()    {        parent::__construct();    }    public function get_services()    {        $sql = 'select * from tbl_service ';        $result = model::doSelect($sql);        return $result;    }    public function get_sampleworks()    {        $sql = 'select * from tbl_samplework ';        $result = model::doSelect($sql);        foreach ($result as $key => $item)        {            $service_id = $item['service_id'];            $sql = 'select * from tbl_service where id=?';            $service = model::doSelect($sql , [$service_id] , false);             $result[$key]['service'] = $service['title'];        }        return $result;    }    public function add_sampleworks(string $title, string $description, array $image , int $service)    {        move_uploaded_file($image['tmp_name'], 'files/' . $image['name']);        $sql = 'insert into tbl_samplework (title , image ,description , service_id ) values(? , ? , ? , ?)';        model::doQuery($sql, [$title, $image['name'], $description , $service]);    }    public function update_sampleworks(int $id, string $title, string $description, array $image , int $service)    {        if ($image['error'] == 0) {            move_uploaded_file($image['tmp_name'], 'files/' . $image['name']);            $sql = 'update tbl_samplework set title=? , image=? ,description=?, service_id=? where id=?';            model::doQuery($sql, [$title, $image['name'], $description , $service, $id]);        } else {            $sql = 'update tbl_samplework set title=? ,description=? , service_id=? where id=?';            model::doQuery($sql, [$title, $description,$service, $id]);        }    }    public function get_samplework($id)    {        $sql = 'select * from tbl_samplework where id=?';        $result = model::doSelect($sql, [$id], false);        return $result;    }    public function delete_samplework($id)    {        $sql = 'delete from tbl_samplework where id=?';        model::doQuery($sql, [$id]);    }    public function paginate()    {        $per_page_record = 6;        if (isset($_GET["page"])) {            $page  = $_GET["page"];        }        else {            $page=1;        }        $start_from = ($page-1) * $per_page_record;        $sql = "SELECT * FROM tbl_samplework limit $start_from ,  $per_page_record ";        $result = model::doSelect($sql);        foreach ($result as $key => $item)        {            $service_id = $item['service_id'];            $sql = 'select * from tbl_service where id=?';            $service = model::doSelect($sql , [$service_id] , false);            $result[$key]['service'] = $service['title'];        }        return $result;    }    public function paginaterows()    {        $sql = "SELECT * FROM tbl_samplework";        $allRows = model::doSelect($sql);        $paginateRows = round(count($allRows ) / 6 )+1;        return $paginateRows;    }}