<?phpclass model_adminservices extends model{    public function __construct()    {        parent::__construct();    }    public function get_services()    {        $sql = 'select * from tbl_service ';        $result = model::doSelect($sql);        return $result;    }    public function add_services(string $title, string $description, array $image)    {        $time = time();        $filename = "$time.".'jpg';        move_uploaded_file($image['tmp_name'],'files/'.$filename );        $sql = 'insert into tbl_service (title , image ,description ) values(? , ? , ?)';        model::doQuery($sql, [$title, $filename, $description]);    }    public function update_services(int $id, string $title, string $description, array $image)    {        if ($image['error'] != 0) {            $sql = 'update tbl_service set title=? ,description=? where id=?';            model::doQuery($sql, [$title, $description, $id]);        } else {            $time = time();            $filename = "$time.".'jpg';            move_uploaded_file($image['tmp_name'],'files/'.$filename );            $sql = 'update tbl_service set title=? , image=? ,description=? where id=?';            model::doQuery($sql, [$title, $filename, $description, $id]);        }    }    public function get_service($id)    {        $sql = 'select * from tbl_service where id=?';        $result = model::doSelect($sql, [$id], false);        return $result;    }    public function delete_service($id , $image)    {        $sql = 'delete from tbl_service where id=?';        model::doQuery($sql, [$id]);        unlink('files/' . $image);    }}