<?phpclass model_login extends model{    public function __construct()    {    }    public function get_admin($email)    {        $sql = 'select * from tbl_admin where email = ?';        $admin = model::doSelect($sql , [$email] , false);        return $admin;    }    public function checkPassword($email , $password)    {        $admin = $this->get_admin($email);        if ($admin['password'] === $password)        {            return true;        }        return false;    }}